        // Thêm ký tự chỉ mục của nó vào mảng
        charindexes.push (str [i]);

        // Cập nhật chuỗi hiện tại
        dòng điện = (dòng điện.length <charindexes.length)? charindexes.join (""): dòng điện;
    }

    // Trả lại đầu ra
    Trả lại dòng điện.length;
}

